const BASE_URL = `${process.env.SERVER_BASE_URL}`

async function getUnread() {
  const baseURL = `${BASE_URL}/getUnread`
  const user = 'user2'
  const url =  `https://braeden-meikle-site-backend.onrender.com/getUnread?user=user2` //`${baseURL}?user=${user}`

  const body = {
      method: 'GET',
      headers: {
        'Content-Type': 'text/json',
        'Access-Control-Allow-Origin': 'no-cors'
      } 
  }

  console.log('constructed req')

  try {
    const response = await fetch(url, body)
    if (!response.ok) {
      throw new Error(`Response status: ${response.status}`)
    }
    console.log('res received')
    console.log(response.text.json)
    return response.json
  } catch (error) {
    console.log(error.message)
  }
}

async function markRead(index) {
  const baseURL = `${BASE_URL}/read`
  const user = 'user2'
  const url = `${baseURL}?user=${user}?read=${index}`

  const body = {
      method: 'POST',
      headers: {
        'Content-Type': 'text/json'
      } 
  }

  console.log('constructed req')

  try {
    const response = await fetch(url, body)
    if (!response.ok) {
      throw new Error(`Response status: ${response.status}`)
    }
    console.log('res received')
    console.log(response.json)
    return response
  } catch (error) {
    console.log(error.message)
  }
}

export { getUnread, markRead }
