function Journies({ sessions }) {
  return (
    <div style={{paddingTop: '10vh', backgroundClip: 'content-box', width: '70vw', height: '70vh', backgroundColor: 'rgb(10,10,10)'}}>
      {sessions.map(session =>
        <Session session={session}/>
      )}
    </div>
  )
}

function Session({ session }) {

  function textFor(result) {
    let color, prefix
    if (result == 0) {
      color = 'rgb(190,190,190)'
      prefix = '  '
    } else {
      color = result > 0 ? 'rgb(0,192,0)' : 'rgb(192,0,0)'
      prefix = result > 0 ? '+ ' : '- '
    }

    return <p style={{width: '70px', color: color}}>{prefix}${result < 0 ? String(result).slice(1) : result}</p>
  }

  return (<div style={{height: '25px', paddingLeft: '20px', display: 'flex', gap: '8px'}}>
    {textFor(session.result)}
    <p style={{color: 'rgb(40,40,40)'}}>&mdash;</p>
    <p style={{color: 'rgb(190,190,190)'}}>{session.stakes} @ {session.loc}</p>
    <p style={{color: 'rgb(40,40,40)'}}>&mdash;</p>
    <p style={{color: 'rgb(190,190,190)'}}>{session.date}</p>
    <p style={{color: 'rgb(40,40,40)'}}>&mdash;</p>
    <p style={{color: 'rgb(190,190,190)'}}>{session.dur}</p>
  </div>)
}

export { Journies }
