  /* Data fetched from backend that will be passed to component before render */
  const [data, setData] = useState(null)

  /* We init our app to loading state */
  const [isLoading, setLoading] = useState(true)

  useEffect(() => {
    const url =  `http://localhost:8000/getUnread?user=user2` //`${baseURL}?user=${user}`

    const body = {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Cache-Control': 'no-cache'
        } 
    }

    console.log('constructed req')

    fetch(url, body).then(response => {
      // console.log(response.text())
      setData(response.text())
      setLoading(false)
    })
  }, [])

  if (isLoading) {
    return <div style={{color: 'white'}}>...Loading</div>
  }

  return (
    <body>
